import React from 'react'
import TimeAgo from 'react-timeago'

class assetPrices extends React.Component {

  getAssetPriceUpDownColor = (assets) =>{
    if(assets.price < assets.history.slice(-2)[0].value){
      return 'red';
    }
    else if(assets.price > assets.history.slice(-2)[0].value){
      return 'green';
    }
    else{
      return null;
    }
  }

  render() {
    let history = this.props.assets.history;
    return (
      <tr className={ this.props.assets.is_selected ? 'selected' : null } id={this.props.assets.assetName} >
        <td>{this.props.asset.assetName.toUpperCase()}</td>
        <td className={this.getAssetPriceUpDownColor(this.props.assets.price)}>
          {this.props.assets.price.toFixed(2)}
        </td>
        <td className='updated_at'>
          <TimeAgo date={ history.slice(-1)[0].time } />
        </td>
      </tr>
    );
  }
}

export default assetPrices;
