  total: 50,
  powder:20,
  backcoutry: 10,
  goal:100
}

const  SkiDayCounter = (total, powder, backcoutry, goal) => {
  return(
    <section>
        <div>
          <p>Total Days: {total}</p>
        </div>
        <div>
          <p>Powder Days: {powder}</p>
        </div>
        <div>
