import React, { Component } from 'react'
import ReactDOM from 'react-dom';
import { render } from 'react-dom'
import './index.css'
import * as serviceWorker from './serviceWorker'
import './App.css';
import $ from 'jquery'
import Rx from 'rxjs/Rx'
import DataTable from 'react-data-table-component';
//import {BootstrapTable, TableHeaderColumn} from 'react-bootstrap-table';
import BootstrapTable from 'react-bootstrap-table-next';
import 'react-bootstrap-table-next/dist/react-bootstrap-table2.min.css';
import filterFactory, { textFilter } from 'react-bootstrap-table2-filter';



const timeObservable = Rx.Observable.interval(9000);
export const mock = Rx.Observable.create((ob) => {
	timeObservable.subscribe(() => {
	  Rx.Observable.from(assets)
		.map(asset => {
		  const random = Math.random();
		  asset.price = random >= 0.5 ? asset.price + random : asset.price - random;
        asset.lastUpdate = Date.now();
		  return asset;
		})
		.subscribe(asset => ob.next(asset));
	});
	return () => null; // we don't care about unsubscribe just for a test
});

const createAsset = (id, type) => {
   return {
     id: id,
     assetName: type === 'Stock' ? ['AAPL','GOOGL','FB', 'TSLA', 'MSFT'][Math.floor(Math.random() * 4)] : ['EUR','USD','GBP', 'NIS', 'AUD'][Math.floor(Math.random() * 4)],
     price: Math.random()*13,
     lastUpdate: Date.now(),//new Date(Date.now()).toString().slice(3,24),
     type: type
   }
 };
 
 const getAllAssets = (n) => {
   const result = [];
   for (let i = 0; i < n; i++) {
     result.push(createAsset(i, 'Stock'));
     result.push(createAsset(i+n, 'Currency'));
   }
   return result;
 }
 
const assets = getAllAssets(200);

const columns = [
  {
    dataField: 'id',
    text: 'id',
    sort: true,
    filter: textFilter()
  },
  {
    dataField: 'assetName',
    text: 'assetName',
    sort: true,
    filter: textFilter()
  },
  {
    dataField: 'price',
    text: 'price',
    sort: true,
    filter: textFilter()
  },
  {
    dataField: 'lastUpdate',
    text: 'lastUpdate',
    sort: true,
    filter: textFilter()
  },
  {
    dataField: 'type',
    text: 'type',
    sort: true,
    filter: textFilter()
  }
];

const defaultSorted = [{
  dataField: 'price',
  order: 'desc'
}];

class Table extends React.Component {
  constructor(props) {
     super(props)
     this.state = {
        assets: getAllAssets(200)
     }
  }

 componentDidMount() {
   const mock = Rx.Observable.create((ob) => {
     timeObservable.subscribe(() => {
       Rx.Observable.from(this.state.assets).map((asset) => {
         const random = Math.random();
         asset.price = random >= 0.5 ? asset.price + random : asset.price - random;
         //this.onSelectedRowsChange = this.updateState;
         //this.onSelectedRowsChange();
         //ReactDOM.render(<Table />, document.getElementById('root'));
         return asset;
       }).subscribe((asset) => ob.next(asset));
     });
     return () => null;
   });
   const p = mock.subscribe((asset) => {
      asset.price = asset.price;
    });
 }


  renderTableHeader() {
     let header = Object.keys(this.state.assets[0])
     return header.map((key, index) => {
        if(key == "lastUpdate") return <th style={{ padding: "15px" }} key={index}>{key.toUpperCase()}</th>
         else return <th style={{ padding: "15px" }} key={index}>{key.toUpperCase()}</th>
     })
  }

  renderTableData() {
     return this.state.assets.map((asset, index) => {
        const { id, assetName, price, lastUpdate, type } = asset //destructuring
        return (
           <tr key={id}>
              <td style={{ padding: "15px" }}>{id}</td>
              <td style={{ padding: "15px" }}>{assetName}</td>
              <td style={{ padding: "15px" }}>{price}</td>
              <td style={{ padding: "15px" }}>{lastUpdate}</td>
              <td style={{ padding: "15px" }}>{type}</td>
           </tr>
        )
     })
  }
/*//                 <tr>{this.renderTableHeader()}</tr>
  //               {this.renderTableData()}
*/
  /*render() {

 

    //const domContainer = document.querySelector('#Table');
     return (
        <div>
           <h1 id='title'>Assets Realtime Details</h1>
           <table id='assets'>
              <tbody>
              <DataTable
                  title="Assets List"
                  columns={columns}
                  data={this.state.assets}
                  defaultSortField="price"
                /> 
              </tbody>
           </table>
        </div>
     )
  }*/

  render(){
    return(
      <div>
      <h1 id='title'>Assets Realtime Details</h1>
    <BootstrapTable
      keyField="id"
      data={ this.state.assets }
      columns={ columns }
      defaultSorted={ defaultSorted } 
    />
    </div>
    )
    
  }
}

ReactDOM.render(<Table />, document.getElementById('root'));

serviceWorker.unregister()
