import React, { Component } from 'react'
import ReactDOM from 'react-dom';
import assetPrices from './assetPrices.js'
import { render } from 'react-dom'
import './index.css'
import * as serviceWorker from './serviceWorker'
//import TableData from './Table.js';
import './App.css';

const elements = []

const createAsset = (assetId, type) => {
   return {
     id: assetId,
     assetName: type === 'Stock' ? ['AAPL','GOOGL','FB', 'TSLA', 'MSFT'][Math.floor(Math.random() * 4)] : ['EUR','USD','GBP', 'NIS', 'AUD'][Math.floor(Math.random() * 4)],
     price: Math.random()*10,
     lastUpdate: Date.now(),
     type: type
   }
 };
 
 const getAllAssets = (n) => {
   const result = [];
   for (let i = 0; i < n; i++) {
     result.push(createAsset(i, 'Stock'));
     result.push(createAsset(i+n, 'Currency'));
   }
   return result;
 }
 
 const assets = getAllAssets(200);



class Table extends React.Component {
  constructor(props) {
     super(props)
     this.state = {
        assets: [
           { id: i, assetName: 'Apple', price: 21, lastUpdate: 'yesterday', type:'Stock' },
           { id: i, assetName: 'Samsung', price: 19, lastUpdate: 'yesterday', type:'Stock' },
           { id: 3, assetName: 'USD', price: 16, lastUpdate: 'yesterday', type:'Currency' },
           { id: 4, assetName: 'INR', price: 25, lastUpdate: 'yesterday', type:'Currency' }
        ]
     }
  }






/*  const Table. getAllAssets = (n) => {
   const result = [];
   for (let i = 0; i < n; i++) {
     result.push(createAsset(i, 'Stock'));
     result.push(createAsset(i+n, 'Currency'));
   }
   return result;
 }*/


  // make this better
  lastUpdatedAt = (lastUpdate) => {
   let time_diff = Date.now() - Number(lastUpdate.slice(-1)[0].time);
   if(time_diff <= 4000){
     // stays 'just now' until this function is called again: WRONG
     return 'just now'
   }
   else if(time_diff > 4000 && time_diff <60000){
      return 'few seconds ago'
   }
   else{
      return Date.now(lastUpdate.slice(-1)[0].time).toLocaleString(undefined, {hour: 'numeric', minute: 'numeric'})
   }
 }

  renderTableHeader() {
     let header = Object.keys(this.state.assets[0])
     return header.map((key, index) => {
        return <th key={index}>{key.toUpperCase()}</th>
     })
  }

  renderTableData() {
     return this.state.assets.map((asset, index) => {
        const { id, assetName, price, lastUpdate, type } = asset //destructuring
        return (
           <tr key={id}>
              <td>{id}</td>
              <td>{assetName}</td>
              <td>{price}</td>
              <td>
              <td className="updated_at">{this.lastUpdatedAt(lastUpdate)}</td>
              {lastUpdate.slice(-1)[0].time }
                  <assetPrices
                    key={index} assetName={assetName}
                    assetData={assetName}
                  />
              </td>
              <td>{type}</td>
           </tr>
        )
     })
  }

  render() {
     return (
        <div>
           <h1 id='title'>React Dynamic Table</h1>
           <table id='assets'>
              <tbody>
                 <tr>{this.renderTableHeader()}</tr>
                 {this.renderTableData()}
              </tbody>
           </table>
        </div>
     )
  }
}

ReactDOM.render(<Table />, document.getElementById('root'));

serviceWorker.unregister()
