import React, { Component } from 'react'
import ReactDOM from 'react-dom';
import assetPrices from './assetPrices.js'
import { render } from 'react-dom'
import './index.css'
import * as serviceWorker from './serviceWorker'
//import TableData from './Table.js';

const elements = []

class Table extends React.Component {
  constructor(props) {
     super(props)
     this.state = {
        assets: [
           { id: 1, assetName: 'Apple', price: 21, lastUpdate: 'yesterday', type:'Stock' },
           { id: 2, assetName: 'Samsung', price: 19, lastUpdate: 'yesterday', type:'Stock' },
           { id: 3, assetName: 'USD', price: 16, lastUpdate: 'yesterday', type:'Currency' },
           { id: 4, assetName: 'INR', price: 25, lastUpdate: 'yesterday', type:'Currency' }
        ]
     }
  }

  renderTableHeader() {
     let header = Object.keys(this.state.assets[0])
     return header.map((key, index) => {
        return <th key={index}>{key.toUpperCase()}</th>
     })
  }

  renderTableData() {
     return this.state.assets.map((asset, index) => {
        const { id, assetName, price, lastUpdate, type } = asset //destructuring
        return (
           <tr key={id}>
              <td>{id}</td>
              <td>{assetName}</td>
              <td>{price}</td>
              <td>
              {Object.keys(props.stocks).map((asset, index) =>
              {
                let current_asset = props.assets[assetName];
                return (
                  <assetPrices
                    key={index} assetName={assetName}
                    assetData={current_asset}
                    toggleStockSelection={props.toggleStockSelection}
                  />
                )
              }
            )}
            { props.areStocksLoaded() ? null : <tr><td colSpan='4'>No stocks loaded yet!</td></tr> }


              </td>
              <td>{type}</td>
           </tr>
        )
     })
  }

  render() {
     return (
        <div>
           <h1 id='title'>React Dynamic Table</h1>
           <table id='assets'>
              <tbody>
                 <tr>{this.renderTableHeader()}</tr>
                 {this.renderTableData()}
              </tbody>
           </table>
        </div>
     )
  }
}

ReactDOM.render(<Table />, document.getElementById('root'));

serviceWorker.unregister()
